import { createAsyncThunk, createSlice } from "@reduxjs/toolkit"
import { loginAPI, registerAPI, refreshAccessTokenAPI } from "./apiProvider"
import { jwtDecode } from "jwt-decode";
import { toast } from "react-toastify";
import { useDispatch } from "react-redux";

export const loginUser = createAsyncThunk("loginUser", async ({ email, password }, { rejectWithValue }) => {
    try {
        return await loginAPI({ email, password })
    } catch (error) {
        return rejectWithValue(error.message);
    }
})

export const registerUser = createAsyncThunk("registerUser", async ({ name, email, password1 }, { rejectWithValue }) => {
    try {
        return await registerAPI({ name, email, password1 })
    } catch (error) {
        return rejectWithValue(error.message)
    }
})

export const refreshAccessToken = createAsyncThunk("refreshAccessToken", async ({ refreshToken }, { rejectWithValue }) => {
    try {
        return await refreshAccessTokenAPI({ refreshToken })
    } catch (error) {
        return rejectWithValue(error.message)
    }
})



const AccountSlice = createSlice({
    name: 'account',
    initialState: {
        user: null,
        is_loading: false,
        is_error: null,
        is_created: null,
        is_authenticated: JSON.parse(localStorage.getItem("authtoken")) || null
    },
    reducers: {
        logout(state) {
            state.user = null
            state.is_authenticated = null
            localStorage.removeItem("authtoken")
            toast.info("Logged Out Successfully!!")

        },
        reset(state) {
            state.user = null
            state.is_loading = false
            state.is_error = null
            state.is_created = null

        }
    },
    extraReducers(builder) {
        builder
            .addCase(loginUser.pending, (state) => {
                state.is_loading = true
            })
            .addCase(loginUser.fulfilled, (state, action) => {
                state.is_loading = false
                state.is_error = null
                state.user = jwtDecode(action.payload.access).name
                state.is_authenticated = action.payload
                localStorage.setItem("authtoken", JSON.stringify(action.payload))
                toast.success(`Welcome ${state.user.toUpperCase()}`)
            })
            .addCase(loginUser.rejected, (state, action) => {
                state.is_loading = false
                state.is_error = action.payload

            })
            .addCase(registerUser.pending, (state) => {
                state.is_loading = true
            })
            .addCase(registerUser.fulfilled, (state, action) => {
                state.is_loading = false
                state.is_error = null
                state.user = null
                state.is_created = action.payload
            })
            .addCase(registerUser.rejected, (state, action) => {
                state.is_loading = false
                state.is_error = action.payload
            })
            .addCase(refreshAccessToken.fulfilled, (state, action) => {
                state.is_authenticated = action.payload
                localStorage.setItem("authtoken", JSON.stringify(action.payload))

            })
            .addCase(refreshAccessToken.rejected, (state, action) => {
                state.is_error = action.payload
                console.log("failed to refresh token")
            })
    },
})

export default AccountSlice.reducer
export const { logout, reset } = AccountSlice.actions

export const StartTokenRefresh = () => {
    const dispatch = useDispatch()
    const refreshToken = localStorage.getItem("authToken")

    if (refreshToken) {

        setInterval(() => {
            console.log("refresh")
            dispatch(refreshAccessToken())
            // dispatch(refreshAccessToken());
        }, 2000);
    }

};
