import { useDispatch, useSelector } from "react-redux";
import "./CSS/ProductList.css"
import { Link, useNavigate } from "react-router-dom";
import { useEffect } from "react";
import { getAllProducts } from "../features/ProductSlice";
import { addCartProduct, getCartProducts } from "../features/CartSlice";

const Check = ({ productId }) => {
    const cartProducts = useSelector(state => state.cart.cart)
    const response = cartProducts.filter((product) => product.id === parseInt(productId))
    if (response.length !== 0) {
        return true
    }
    return false
}

function ProductList() {
    const dispatch = useDispatch();
    const navigate = useNavigate()
    useEffect(() => {
        dispatch(getAllProducts())
        dispatch(getCartProducts())
    }, [dispatch])
    const Products = useSelector((state) => state.product.products)
    return (
        <div className="container" style={{ marginTop: "20px" }}>
            <div className="row">
                {Products &&
                    Products.map((product) => (
                        <div className="col-md-3 my-3" key={product.id}>
                            <div className="card-sl">
                                <div className="card-image ">
                                    <img
                                        src={product.image}
                                        alt="Product"
                                        onClick={() =>
                                            navigate(`${product.id}`)
                                        }
                                    />
                                </div>
                                <Link className="card-action">
                                    <i className="fa fa-heart fa-2x"></i>
                                </Link>
                                <div className="card-heading">{product.name}</div>
                                <div className="card-text">
                                    {product.description.slice(0, 90)}...
                                </div>
                                <div className="card-text">&#8377; {product.price}</div>

                                {Check(product.id) ?
                                    <Link
                                        className="card-button text-decoration-none"
                                        onClick={() => navigate("/cart")}
                                    >
                                        Go to Cart
                                    </Link> :
                                    <Link
                                        className="card-button text-decoration-none"
                                        onClick={() => dispatch(addCartProduct(product.id))}
                                    >
                                        Add to Cart
                                    </Link>
                                }


                            </div>
                        </div>
                    ))}
            </div>
        </div>
    );
}

export default ProductList;
