import React, { useEffect, useState } from "react";
import { Container, Col, Card, Form, Button, Spinner } from "react-bootstrap";
import { useDispatch, useSelector } from "react-redux";
import { reset } from "../features/AccountSlice";
import { Link } from "react-router-dom";
import { toast } from "react-toastify";
import { loginUser } from "../features/apiProvider";

function LoginPage() {
    const dispatch = useDispatch()
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const { status, error } = useSelector((state) => state.account);


    const handleLogin = (e) => {
        e.preventDefault();
        dispatch(loginUser({ email, password }))

    }
    useEffect(() => {
        if (status === 'rejected') {
            toast.error(error)
            dispatch(reset())
        }

    }, [status, dispatch, error])

    return (
        <Container
            fluid
            className="d-flex justify-content-center align-items-center h-100"
        >
            <Col xs={12} md={6}>
                <Card
                    className="bg-dark text-white my-3 mx-auto"
                    style={{
                        borderRadius: "1rem",
                        maxWidth: "510px",
                        maxHeight: "550px",
                    }}
                >
                    <Card.Body className=" d-flex flex-column align-items-center mx-auto w-100">
                        <h2 className="fw-bold mb-2 text-uppercase">Login</h2>
                        <p className="text-white-50 mb-5">
                            Please enter your login and password!
                        </p>
                        <form
                            className="d-flex flex-column align-items-center"
                            onSubmit={handleLogin}
                        >
                            <Form.Group
                                className="mb-4 mx-5 w-100"
                                controlId="formBasicEmail"
                            >
                                <Form.Label className="text-white">Email address</Form.Label>
                                <Form.Control
                                    type="email"
                                    name="email"
                                    size="lg"
                                    onChange={e => setEmail(e.target.value)}
                                    value={email}
                                    autoComplete="username"
                                    required
                                />
                            </Form.Group>

                            <Form.Group
                                className="mb-4 mx-5 w-100"
                                controlId="formBasicPassword"
                            >
                                <Form.Label className="text-white">Password</Form.Label>
                                <Form.Control
                                    type="password"
                                    name="password"
                                    value={password}
                                    size="lg"
                                    onChange={e => setPassword(e.target.value)}
                                    autoComplete="current-password"
                                    required
                                />
                            </Form.Group>
                            <Button
                                variant="outline-light"
                                type="submit"
                                size="lg"
                                className="m-2 px-5"

                            >
                                {status === 'loading' ? <Spinner /> : "Login"}
                            </Button>
                            <p>
                                Are you new here ? <Link to="/register" style={{ marginLeft: "5px", textDecoration: "none", fontWeight: "bold", color: "orange" }} >Register</Link>
                            </p>
                        </form>
                    </Card.Body>
                </Card>
            </Col>
        </Container>
    );
}

export default LoginPage;
