import { createAsyncThunk, createSlice, isRejectedWithValue } from "@reduxjs/toolkit"
import axios from "axios"

export const loginUser = createAsyncThunk("loginUser", async ({ email, password }) => {
    try {
        const response = await axios.post("http://127.0.0.1:8000/api/account/token/", {
            email: email,
            password: password
        })

        return response.data
    } catch (error) {
        if (!error.response) {
            // Network error (e.g., no internet connection)
            console.log("Network error:", error.message)
            return ("Network error occurred. Please try again.")
        }

        // Request was made but server responded with status other than 2xx
        if (error.response.status === 401) {
            console.log("Unauthorized - Incorrect email or password")
            return ("Incorrect email or password. Please try again.")
        }

        // Other errors
        console.error("An error occurred:", error)
        return ("An error occurred. Please try again later.")
    }



})

const AccountSlice = createSlice({
    name: 'account',
    initialState: {
        user: null,

        is_loading: false,
        is_error: null
    },
    extraReducers(builder) {
        builder
            .addCase(loginUser.pending, (state) => {
                state.is_loading = true
            })
            .addCase(loginUser.fulfilled, (state, action) => {
                state.is_loading = false
                state.user = action.payload
            })
            .addCase(loginUser.rejected, (state, action) => {
                state.is_loading = false
                state.is_error = action.payload
            })
    },
})

export default AccountSlice.reducer
