import { useLocation, useParams } from "react-router-dom";
import { Button, Card, Col, Row } from "react-bootstrap";
import { useDispatch, useSelector } from "react-redux";
import { useEffect, useState } from "react";
import { ProductDetailsAPI } from "../features/apiProvider";

export default async function ProductDetails() {
    const { productId } = useParams()


    const productData = await ProductDetailsAPI({ productId })
    console.log(productData)
    const { state: data } = useLocation();
    const dispatch = useDispatch();
    const [index, setIndex] = useState(0);
    const wishlist = useSelector((state) => state.product.wishlist);
    const color = handleColor();

    function handleColor() {
        return wishlist.some((item) => item.id === data.id) ? "red" : "#5a5a5a";
    }

    return (
        <Card className="m-auto mt-3">
            <Row>
                <Col xs={1} style={{ alignSelf: "center" }}>

                    <Card.Img
                        style={{ height: "fit-content", width: "100%", padding: "1%" }}
                        src={productData.image}
                    // key={index}
                    // onClick={() => setIndex(index)}
                    />

                </Col>
                {/* <Col xs={4}>
                    <Card.Img src={data.images[index]} />
                </Col> */}
                <Col xs={6}>
                    <Card.Body>
                        <Card.Title style={{ fontSize: "40px" }}>{productData.name}</Card.Title>
                        <Card.Text>{productData.description}</Card.Text>
                        <span style={{ fontSize: "1rem", padding: "15px" }}>
                            &#8377; {productData.price}
                        </span>
                    </Card.Body>
                    <Button
                        variant="secondary"
                        style={{ margin: "20px 20px" }}
                    // onClick={() => AddCartClick(dispatch, data)}
                    >
                        Add to Cart
                    </Button>
                </Col>
                <Col xs={1}>
                    <i
                        className="fa fa-heart fa-2x"
                        aria-hidden="true"
                        // onClick={() => AddWishlistClick(wishlist, dispatch, data)}
                        style={{ color: color }}
                    ></i>
                </Col>
            </Row>
        </Card>
    );
}
