import { useParams } from "react-router-dom";
import { Button, Card, Col, Row } from "react-bootstrap";
import "./CSS/ProductDetails.css"

import { ProductDetailsAPI } from "../features/apiProvider";
import { useEffect, useState } from "react";
import { toast } from "react-toastify";

export default function ProductDetails() {
    const { productId } = useParams()
    const [productData, setProductData] = useState({})
    const [error, setError] = useState()


    useEffect(() => {
        const fetchData = async () => {
            try {
                const data = await ProductDetailsAPI({ productId })
                setProductData(data)

            } catch (error) {
                setError(error.message)
                toast.error(error.message)
            }

        }
        fetchData()
    }, [productId])



    return (
        <Card className="m-auto mt-3">
            {error ? <div> error</div> :
                <Row>
                    <Col xs={1} style={{ alignSelf: "center" }}>

                        <Card.Img
                            style={{ height: "fit-content", width: "100%", padding: "1%" }}
                            src={productData.image}
                        // key={index}
                        // onClick={() => setIndex(index)}
                        />

                    </Col>
                    <Col xs={4}>
                        <Card.Img src={productData.image} />
                    </Col>
                    <Col xs={6}>
                        <Card.Body>
                            <Card.Title style={{ fontSize: "40px" }}>{productData.name}</Card.Title>
                            <Card.Text>{productData.description}</Card.Text>
                            <span style={{ fontSize: "1rem", padding: "15px" }}>
                                &#8377; {productData.price}
                            </span>
                        </Card.Body>
                        <Button
                            variant="secondary"
                            style={{ margin: "20px 20px" }}
                        // onClick={() => AddCartClick(dispatch, data)}
                        >
                            Add to Cart
                        </Button>
                    </Col>
                    <Col xs={1}>
                        <i
                            className="fa fa-heart fa-2x"
                            aria-hidden="true"
                        // onClick={() => AddWishlistClick(wishlist, dispatch, data)}
                        // style={{ color: color }}
                        ></i>
                    </Col>
                </Row>}
        </Card>
    );
}
